#!/usr/bin/bash

# .csv (generated by JCR as those from Steven Bell) -> .tsv  (can be handled by GitHub)

chmod -x csv/*

ls csv | \
xargs -l basename -s .csv | \
parallel "
   awk '/Rank|Journal name/,/Copyright/' csv/{}.csv | \
   awk '!/^$/ && !/Copyright/' | \
   sed -r ':L;s=\b([0-9]+),([0-9]+)\b=\1\2=g;t L' | \
   tr ',' '\t'> {}.tsv
"
